mapper.select:
select tno, title, dueDate, writer, finished
from tbl_todo
where tno = #{tno}

mapper.update:
update tbl_todo
set title    = #{title},
    dueDate  = #{dueDate},
    finished = #{finished}
where tno = #{tno}

mapper.select:
select tno, title, dueDate, writer, finished from tbl_todo




                title like concat('%', #{searchContent}, '%')


                writer like concat('%', #{searchContent}, '%')





            finished = 1




            dueDate between #{startDate} and #{endDate}



order by tno desc limit #{skip}, #{size}

select count(tno) from tbl_todo




                title like concat('%', #{searchContent}, '%')


                writer like concat('%', #{searchContent}, '%')





            finished = 1




            dueDate between #{startDate} and #{endDate}



order by tno desc limit #{size}
